import { Component } from "react";
import { ViewStyle, StyleProp } from "react-native";

interface Point<T = number> {
  x: T,
  y: T
}

interface Size<T = number> {
  width: T;
  height: T;
}

export interface Event {
  data: any,
  /**
   * @description For Android use `[Point<string>, Point<string>]`
   * @description For iOS use `{ origin: Point<string>, size: Size<string> }`
   */
  bounds: [Point<string>, Point<string>] | { origin: Point<string>, size: Size<string> }
}

export interface RNMaskCpfCnpj {
        containerStyle?: StyleProp<ViewStyle>;

        inputStyle?: StyleProp<ViewStyle>;
        placeholder?: string;
        placeholderTextColor?: string;
        cpf?: boolean;
        cnpj?: boolean;
        keyboardTypeSubmit?: 'next' | 'done' | 'send' | 'none';
        /* A aparência do botão de submit */
        autoFocus(e: Event): void;
        onFocus(e: Event): void;
        submit(e: Event): void;
        setData(e: Event): void;
        submit(e: Event): void;
}



export interface RNMaskCpfCnpjState {
  readonly formatted: boolean;
  readonly masker: any;
}

export default class MaskCpfCnpj extends Component<RNMaskCpfCnpj, RNMaskCpfCnpjState> {
    _renderInput(): JSX.Element | null;
}
